(function() {
  var path;

  path = require('path');

  module.exports = function(name, endings) {
    var pattern, regex;
    if (endings == null) {
      endings = 'js|coffee';
    }
    pattern = "(\.(" + endings + ")$)";
    regex = new RegExp(pattern, 'i');
    return regex.test(path.extname(name));
  };

}).call(this);
